// Get the skills list element
const skillsList = document.querySelector(".skills-list");

// Map through the skills array and create a button element for each skill
const skillsHTML = skills
  .map(
    (skill) => `
  <button type="button" class="skill-button">
    ${skill.svg}
    ${skill.title}
  </button>
`
  )
  .join("");

// Set the HTML of the skills list element
skillsList.innerHTML = skillsHTML;
